<script setup>
import {loadThings} from "@/components/script.js";
import { ref, computed, watch, reactive} from 'vue';
import { useRouter } from 'vue-router';
import axios from 'axios';
let showRecipeName = true;
let showForm = ref(false);
let showFavoritesOnly = ref(false);
let selectedCuisine = ref('');
let selectedMealTime = ref('');
let selectedDietType = ref('');
let editIndex = ref(null);
let recipes =  reactive([
  {
    name: "Spaghetti Carbonara",
    cuisine: "Italian",
    mealTime: "Dinner",
    dietType: "with Meat",
    picture: "data:image/jpeg;base64,",
    preparationTime: "20 Minuten",
    cookingTime: "15 Minuten",
    description: "Spaghetti Carbonara ist ein klassisches italienisches Nudelgericht, das in der Regel aus Spaghetti, Guanciale, Ei, Pecorino Romano und Pfeffer zubereitet wird. Die Zutaten werden in einer Pfanne gemischt, bis sie eine cremige Konsistenz haben. Das Gericht wird oft mit zusätzlichem Pecorino Romano und Pfeffer garniert.",
    ingredients: [
      "400g Spaghetti",
      "200g Guanciale",
      "4 Eier",
      "100g Pecorino Romano",
      "Pfeffer"
    ],
    instructions: [
      "Die Spaghetti in einem großen Topf mit kochendem Salzwasser al dente kochen.",
      "In der Zwischenzeit das Guanciale in einer Pfanne bei mittlerer Hitze knusprig braten.",
      "Die Eier in einer Schüssel verquirlen und den geriebenen Pecorino Romano hinzufügen.",
      "Die gekochten Spaghetti abgießen und in die Pfanne mit dem Guanciale geben. Die Ei-Käse-Mischung darüber gießen und gut vermengen.",
      "Mit Pfeffer würzen und sofort servieren."
    ],
    favorite: false
  },
  {
    name: "Ratatouille",
    cuisine: "French",
    mealTime: "Main Course",
    dietType: "Vegetarian",
    picture: "data:image/jpeg;base64,",
    preparationTime: "30 Minuten",
    cookingTime: "45 Minuten",
    description: "Ratatouille ist ein französisches Gemüsegericht, das aus Auberginen, Zucchini, Paprika, Tomaten, Zwiebeln und Knoblauch zubereitet wird. Die Gemüsesorten werden in einer Pfanne geschichtet und im Ofen gebacken, bis sie weich sind. Das Gericht wird oft mit frischen Kräutern wie Thymian und Basilikum garniert.",
    ingredients: [
      "1 Aubergine",
      "1 Zucchini",
      "1 rote Paprika",
      "1 gelbe Paprika",
      "2 Tomaten",
      "2 Zwiebeln",
      "2 Knoblauchzehen",
      "Thymian",
      "Basilikum",
      "Olivenöl"
    ],
    instructions: [
      "Die Aubergine, Zucchini, Paprika und Tomaten in dünne Scheiben schneiden.",
      "Die Zwiebeln und Knoblauchzehen fein hacken.",
      "In einer Pfanne Olivenöl erhitzen und die Zwiebeln und Knoblauchzehen darin anbraten.",
      "Die Gemüsescheiben abwechselnd in einer Auflaufform schichten.",
      "Mit Thymian und Basilikum bestreuen und im vorgeheizten Ofen bei 180°C ca. 45 Minuten backenn."
    ],
    favorite: false
  }
]);
let newRecipe ={
  name: "",
  picture: "",
  preparationTime: "",
  cookingTime: "",
  description: "",
  ingredients: [""],
  instructions: [""],
  cuisine: "",
  mealTime: "",
  dietType: ""
};

async function addRecipe() {
  newRecipe.ingredients = newRecipe.ingredients.split('\n');
  newRecipe.instructions = newRecipe.instructions.split('\n');

  const endpoint = 'https://meinkochbuch-backend.onrender.com/kochbuch';


  const data = {
    name: newRecipe.name,
    cuisine: newRecipe.cuisine,
    mealTime: newRecipe.mealTime,
    dietType: newRecipe.dietType,
    preparationTime: newRecipe.preparationTime,
    cookingTime: newRecipe.cookingTime,
    description: newRecipe.description,
    ingredients: newRecipe.ingredients,
    instructions: newRecipe.instructions,
    picture: newRecipe.picture
  };


  const requestOptions = {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(data)
  };


  try {
    const response = await fetch(endpoint, requestOptions);
    const responseData = await response.json();
    console.log('Success:', responseData);
  } catch (error) {
    console.error('Error:', error);
  }

  recipes.push({...newRecipe});

  selectedCuisine.value = newRecipe.dishType;
  selectedMealTime.value = newRecipe.mealTime;
  selectedDietType.value = newRecipe.dietType;

  newRecipe = {
    name: "",
    picture: "",
    preparationTime: "",
    cookingTime: "",
    description: "",
    ingredients: [""],
    instructions: [""],
    dishType: "",
    mealTime: "",
    dietType: ""
  };
  showForm.value = false;
}
function showAddRecipeForm() {
  showForm.value = true;
}
function deleteRecipe(index) {
  console.log("deleteRecipe function called with index: ", index);
  recipes.splice(index, 1);
}

let cuisine = computed(() => {
  let types = recipes.map(recipe => recipe.cuisine);
  return [...new Set(types)];
});
let mealTimes = computed(() => {
  let times = recipes.map(recipe => recipe.mealTime);
  return [...new Set(times)];
});

let dietTypes = computed(() => {
  let types = recipes.map(recipe => recipe.dietType);
  return [...new Set(types)];
});
let filteredRecipes = computed(() => {
  return recipes.filter(recipe => {
    return (!showFavoritesOnly.value || recipe.favorite) &&
      (selectedCuisine.value === '' || recipe.cuisine === selectedCuisine.value) &&
      (selectedMealTime.value === '' || recipe.mealTime === selectedMealTime.value) &&
      (selectedDietType.value === '' || recipe.dietType === selectedDietType.value);
  });
});

function editRecipe(index) {
  newRecipe = {...recipes[index]};
  editIndex.value = index;
  showForm.value = true;
}

function updateRecipe() {
  recipes[editIndex.value] = {...newRecipe};
  resetForm();
}

function resetForm() {
  newRecipe = {
    name: "",
    picture: "",
    preparationTime: "",
    cookingTime: "",
    description: "",
    ingredients: [""],
    instructions: [""],
    dishType: "",
    mealTime: "",
    dietType: ""
  };
  showForm.value = false;
  editIndex.value = null;
}

let shoppingList = ref([]); // Define shoppingList here
const router = useRouter();

function generateShoppingList(recipe) {
  shoppingList.value = recipe.ingredients;
  let shoppingListParam = shoppingList.value.length > 0 ? JSON.stringify(shoppingList.value) : "[]";
  router.push({
    name: 'ShoppingList',
    params: { shoppingList: shoppingListParam }
  }, { replace: false });
}
function toggleFavorite(index) {
  recipes[index].favorite = !recipes[index].favorite;
}








</script>

<template>
  <div class="recipeheader">
    <h1>Rezepte</h1>
    <button @click="loadThings">Load recipes</button>
    <button @click="showAddRecipeForm">Add Recipe</button>
    <label for="filterFavorites">Show Favorites Only:</label>
    <input type="checkbox" id="filterFavorites" v-model="showFavoritesOnly" />
    <label for="cuisine">Cuisine:</label>
    <select id="cuisine" v-model="selectedCuisine">
      <option value="">All</option>
      <option v-for="cuisine in cuisine" :key="cuisine" :value="cuisine">
        {{ cuisine }}
      </option>
    </select>
    <label for="mealTime">Meal Time:</label>
    <select id="mealTime" v-model="selectedMealTime">
      <option value="">All</option>
      <option v-for="mealTime in mealTimes" :key="mealTime" :value="mealTime">
        {{ mealTime }}
      </option>
    </select>
    <label for="dietType">Diet Type:</label>
    <select id="dietType" v-model="selectedDietType">
      <option value="">All</option>
      <option v-for="dietType in dietTypes" :key="dietType" :value="dietType">
        {{ dietType }}
      </option>
    </select>
    <div class="addrecipe">
      <form @submit.prevent="addRecipe" v-if="showForm">
        <label>Name: <input v-model="newRecipe.name" required /></label>
        <label>Cuisine: <input v-model="newRecipe.cuisine" required /></label>
        <label>Meal Time: <input v-model="newRecipe.mealTime" required /></label>
        <label>Diet Type: <input v-model="newRecipe.dietType" required /></label>
        <label>Preparation Time: <input v-model="newRecipe.preparationTime" required /></label>
        <label>Cooking Time: <input v-model="newRecipe.cookingTime" required /></label>
        <label>Description: <input v-model="newRecipe.description" required /></label>
        <label>Ingredients: <textarea v-model="newRecipe.ingredients" required></textarea></label>
        <label>Instructions: <textarea v-model="newRecipe.instructions" required></textarea></label>
        <label>Picture: <input v-model="newRecipe.picture"  /></label>
        <button v-if="editIndex === null" @click="addRecipe">Add Recipe</button>
        <button v-else @click="updateRecipe">Update Recipe</button>
      </form>
    </div>
  </div>
  <div class="newRecipe" v-for="(recipe, index) in filteredRecipes" :key="index">
    <h2>{{ recipe.name }}</h2>
    <button @click="toggleFavorite(index)">
      {{ recipe.favorite ? 'Unmark Favorite' : 'Mark as Favorite' }}
    </button>
    <button @click="deleteRecipe(index)">Delete Recipe</button>
    <button @click="editRecipe(index)">Edit Recipe</button>
    <button @click="generateShoppingList(recipe)">Generate Shopping List</button>
    <h6>Cuisine</h6>
    <p>{{ recipe.cuisine }}</p>
    <h6>Meal Time</h6>
    <p>{{ recipe.mealTime }}</p>
    <h6>Diet Type</h6>
    <p>{{ recipe.dietType }}</p>
    <img :src="recipe.picture" alt="Picture of the recipe" />
    <h3>Description</h3>
    <p>{{ recipe.description }}</p>
    <h3>Preparation Time</h3>
    <p>{{ recipe.preparationTime }}</p>
    <h3>Cooking Time</h3>
    <p>{{ recipe.cookingTime }}</p>
    <h3>Ingredients</h3>
    <ul>
      <li v-for="(ingredient, index) in recipe.ingredients" :key="index">{{ ingredient }}</li>
    </ul>
    <h3>Instructions</h3>
    <ul>
      <li v-for="(instruction, index) in recipe.instructions" :key="index">{{ instruction }}</li>
    </ul>
  </div>




</template>

<style>
.addrecipe
{
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 1rem;
  margin-top: 3rem;
  padding: 50px;
}
.addrecipe button[type="submit"] {
  margin-top: 20px;
  align-items: flex-start;
}

.newRecipe{
  margin-top: 0rem;
  display: flex;
  flex-direction: column;
  align-items: flex-start; /* Adjust this line */
  gap: 20px; /* Adjust as needed */
  background-color: whitesmoke;
  padding: 10px;
}
.recipeheader {
  min-height: 30vh;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  background-color: lightcyan;
}
@media (min-width: 1024px) {
  .recipeheader {
    height: 100vh;
    justify-content: center;
    display: flex;
    align-items: center;
  }
}
</style>